sys_script_include.script:
var CryptoUtil = Class.create();
CryptoUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCryptoPrice: function() {
        var cryptoSysId = this.getParameter("sysparm_sysId");
        var gr = new GlideRecord('x_350882_cryptonow_crypto_listings');
        gr.addQuery('sys_id', cryptoSysId);
        gr.query();
        if (gr.next()) {
            return gr.getValue('price');
        }
    },
    type: 'CryptoUtil'
});
